import fs from 'fs'
import path from 'path'
import matter from 'gray-matter'
import remark from 'remark'


export async function getSortedPostsData() {
  const res = await fetch('https://hn.algolia.com/api/v1/search_by_date?tags=story,front_page')
  const posts = await res.json();
  console.log(posts)
  return posts.hits.map(post => {
    return {
        id: post.story_id,
        url: post.url,
        title: post.title
    }
  })
}
